function baseaddress(mokuai,offset,flags,value,freeze,name)  if mokuai==true then  zf=[[""]]  print("教程:\nbaseaddress(模块名[例如:libUE4.so,要加''或"..zf.."],偏移量[可以使用0x偏移量(十六进制)也可以使用不加0x的十进制 PS:GG自带的计算偏移量获取得是十六进制偏移量,不用加''和"..zf.."],修改类型[数字类型或字母类型],修改值[不用加''和"..zf.."],是否冻结[true开启和false关闭],功能名字[要加''或"..zf.."])")  else  if mokuai==nil then  gg.toast("模块名不可空")  else  if offset==nil then  gg.toast("偏移量不可空")  else  if flags==nil then  gg.toast("类型不可空")  else  if value==nil then  gg.toast("修改值不可空")  else  if freeze==nil then  gg.toast("是否冻结不可空")  else  if freeze==nil then  gg.toast("功能名字不可空")  else  function baseaddressedit(address,flags,value,freeze,name)  tt={}  tt[1]={}  tt[1].address=address  tt[1].flags=flags  yuansz=tt[1].freeze  if yuansz==value then  q=1  end  tt[1].value=value  tt[1].freeze=freeze  gg.setValues(tt)  if yuansz~=value then  gg.toast(name.."开启成功")  else  if q==1 then  gg.toast(name.."开启成功")  end  end  end  so=gg.getRangesList(mokuai)[1].start  py=offset  baseaddressedit(so+py,flags,value,freeze,name)  end  end  end  end  end  end  end  end

